import React, { useEffect,useState } from 'react'
import axios from 'axios';
import './Card.css'



const Card = () => {
    const [name, setName] = useState("");
  const [large, setLarge] = useState("");
  const [email, setEmail] = useState("");
  const [phone, setPhone] = useState("");
  const [city, setCity] = useState("");
  const [age, setAge] = useState("");
  const [date, setDate] = useState("");


  const randomUser =() =>{
    var config = {
        method: 'get',
        url: 'https://randomuser.me/api',
        headers: { }
      };

      axios(config)
      .then(function (res) {
    setName(res.data.results[0].name);
    setLarge(res.data.results[0].picture.large);
    setEmail(res.data.results[0].email);
    setPhone(res.data.results[0].phone);
    setCity(res.data.results[0].location.city);
    setAge(res.data.results[0].dob.age);
    setDate(res.data.results[0].registered.date);
        })
      .catch(function (error) {
        console.log(error);
      });

  }

  useEffect(()=>{
      randomUser();
  })






  return (
      <>
   <div className="center">
<div className="card green">
  <div className="additional">
    <div className="user-card">
      <div className="level center">
        Level 13
      </div>
      <div className="points center">
        5,312 Points
      </div>
      <svg style={{
        backgroundImage: url(res.data.results[0].picture.large)
      }}width="110" height="110" viewBox="0 0 250 250" xmlns="http://www.w3.org/2000/svg" role="img" aria-labelledby="title desc" className="center">
        <title id="title">Teacher</title>
        <desc id="desc">Cartoon of a Caucasian woman smiling, and wearing black glasses and a purple shirt with white collar drawn by Alvaro Montoro.</desc>

        <defs>
          <clipPath id="scene">
            <circle cx="125" cy="125" r="115"/>
          </clipPath>
          <clipPath id="lips">
            <path d="M 106,132 C 113,127 125,128 125,132 125,128 137,127 144,132 141,142  134,146  125,146  116,146 109,142 106,132 Z" />
          </clipPath>
        </defs>
        <circle  cx="125" cy="125" r="120" fill="rgba(0,0,0,0.15)" />
        <g stroke="none" strokeWidth="0" clipPath="url(#scene)">


        </g>
      </svg>
    </div>
    <div className="more-info">
      <h1>Jane Doe</h1>
      <div className="coords">
        <span>Group Name</span>
        <span>Joined January 2019</span>
      </div>
      <div className="coords">
        <span>Position/Role</span>
        <span>City, Country</span>
      </div>
      <div className="stats">
        <div>
          <div className="title">Awards</div>
          <i className="fa fa-trophy"></i>
          <div className="value">2</div>
        </div>
        <div>
          <div className="title">Matches</div>
          <i className="fa fa-gamepad"></i>
          <div className="value">27</div>
        </div>
        <div>
          <div className="title">Pals</div>
          <i className="fa fa-group"></i>
          <div className="value">123</div>
        </div>
        <div>
          <div className="title">Coffee</div>
          <i className="fa fa-coffee"></i>
          <div className="value infinity">∞</div>
        </div>
      </div>
    </div>
  </div>
  <div className="general">
    <h1>Jane Doe</h1>
    <p>Lorem ipsum dolor sit amet, consectetur adipiscing elit. Fusce a volutpat mauris, at molestie lacus. Nam vestibulum sodales odio ut pulvinar.</p>
    <span className="more">Mouse over the card for more info</span>
  </div>
</div>

</div>

    </>

  )
}

export default Card
