import axios from 'axios';
import './Card.css'



const Card = () => {

    const [name, setName] = useState("");
  const [large, setLarge] = useState("");
  const [email, setEmail] = useState("");
  const [phone, setPhone] = useState("");
  const [city, setCity] = useState("");
  const [age, setAge] = useState("");
  const [date, setDate] = useState("");
  const [gender, setGender] = useState("");
  console.log(large);


  const randomUser =() =>{
    var config = {
        method: 'get',
        url: 'https://randomuser.me/api',
        headers: { }
      };

      axios(config)
      .then(function (res) {
    setName(res.data.results[0].name);
    setLarge(res.data.results[0].picture.large);
    setEmail(res.data.results[0].email);
    setPhone(res.data.results[0].phone);
    setCity(res.data.results[0].location.city);
    setAge(res.data.results[0].dob.age);
    setDate(res.data.results[0].registered.date);
    setGender(res.data.results[0].gender);

        })
      .catch(function (error) {
        console.log(error);
      });

  }

  useEffect(() => {
      randomUser();


  },[]);






  return (
      <>
   <div className="center">
<div className="card green">
  <div className="additional">
    <div className="user-card">

      <div className="points center">
        5,312 Points
      </div>
      <svg style={{ backgroundImage: `url(${large})` }} width="110" height="110" viewBox="0 0 250 250" xmlns="" role="img" aria-labelledby="title desc" className="center">
        <title id="title">Teacher</title>
        <desc id="desc">Cartoon of a Caucasian woman smiling, and wearing black glasses and a purple shirt with white collar drawn by Alvaro Montoro.</desc>


        <g stroke="none" strokeWidth="0" clipPath="url(#scene)">


        </g>
      </svg>
    </div>
    <div className="more-info">
      <h1>dfsdfsf</h1>
      <div className="coords">
        <span>Group Name</span>
        <span>Joined January 2019</span>
      </div>
